import React from "react";
import API from "../../utilities/API";
import { Container } from "../../components/Grid/Container";
import { Row } from "../../components/Grid/Row";


class Reports extends React.Component {

    state = {
        drugs: [],
        ndcNum: "",
        rxNum: "",
        pharmName: "",
        doctorName: "",
        drugName: "",
        drugForm: "",
        drugFormSize: "",
        drugFormMeasure: "",
        currentQty: "",
        bottleFullQty: "",
        bottlePartialQuantity: "",
        rxDiscard: "",
        rxReorder: "",
        drugDose: "",
        drugFreq: "",
        early: "",
        middle: "",
        late: "",
        instructions: "",
        precautions: ""
    }

    componentDidMount() {
        this.loadDrugs();
    }

    loadDrugs = () => {
        API.getDrugs()
            .then(res =>
                this.setState({
                    drugs: res.data,

                })
            )
            .catch(err => console.log(err))
    };

    handleInputChange = event => {
        let value = event.target.value
        const name = event.target.name

        this.setState({
            [name]: value
        })

    }

    render() {
        return (
            <Container fluid>
                <Row>
                    <list>
                        {this.state.drugs.map(drug => (
                            <ol>
                                <li key={drug._id}>
                                    {this.state.drugs}
                                </li>
                            </ol>
                        ))}
                    </list>

                </Row>
            </Container>
        )
    }

}

export default Reports;
