import axios from "axios";
const BASEURL = "https://api.fda.gov/drug/label.json?search=openfda.brand_name:";
const queryURL = BASEURL + drugName;


export default {
    // Gets all drugs
    getDrugs: function() {
      return axios.get("/api/drug");
    },
    // Gets the drug with the given id
    getDrug: function(id) {
      return axios.get("/api/drug/" + id);
    },
    // Deletes the drug with the given id
    deleteDrug: function(id) {
      return axios.delete("/api/drug/" + id);
    },
    // Saves a drug to the database
    saveDrug: function(drugData) {
      return axios.post("/api/drug", drugData);
    },
    searchFDA: function(drugName){
      return axios.get("https://api.fda.gov/drug/label.json?search=openfda.brand_name:" + drugName);
    }
  };
