import React, { Component, Fragment } from "react";

/* Import Components */
import CheckBox from "../../components/checkBox/CheckBox";
import Input from "../../components/input/Input";
import TextArea from "../../components/textArea/TextArea";
import Select from "../../components/select/Select";
import Button from "../../components/button/Button";
import Wrapper from "../../components/wrapper/Wrapper";
import { Col, Row, Container } from "../../components/Grid";
import API2 from "../../utilities/API2";

// import {
//   Row, Col, Card, CardBlock, Container, CardTitle, CardText
// } from 'reactstrap';


class User extends Component {
  // constructor(props) {
  //   super(props);

// component did mount goes below the states.
state = {
  inventory: [],
  drugname: "",
  bottleFullQuantity: "",
  bottlePartialQuantity: "",
  drugDose: "",
  drugFrequency: "",
}


componentDidMount() {
  this.loadInventory(); 
}

loadInventory = () => {
  API2.getInventories()
  .then(res =>
    this.setState({ inventory:res.data, 
    drugname: res.data.drugname,
    bottleFullQuantity: "",
    bottlePartialQuantity: "",
    drugDose: "",
    drugFrequency: "",
   })
    )
    .catch(err => console.log(err))
}


render () {
  return (
    <Wrapper>
      <Fragment>
        <Container fluid>
          <Row>
            <Col>
            <h1>
              {this.state.inventory.drugname} has {this.bottlePartialQuantity} left
            </h1>
            </Col>
          </Row>
        </Container>
      </Fragment>
    </Wrapper>
  )
}

}
export default User;
