//         currentQty: 60,
//         bottleFullQty: 90,
//         bottlePartialQuantity: 45,
//         rxDiscard: "2018-10-07",
//         rxReorder: "2018-10-04",
//         drugDose: 1,
//         drugFreq: 3,
//         early: false,
//         middle: false,
//         late: true,
//         instructions: "take with 3 glasses of water",
//         precautions: "stay close to a bathroom"


class Reports extends React.Component {

    state = {
        drugs: [],
        ndcNum: "",
        rxNum: "",
        pharmName: "",
        doctorName: "",
        drugName: "",
        drugForm: "",
        drugFormSize: "",
        drugFormMeasure: "",
        currentQty: "",
        bottleFullQty: "",
        bottlePartialQuantity: "",
        rxDiscard: "",
        rxReorder: "",
        drugDose: "",
        drugFreq: "",
        early: "",
        middle: "",
        late: "",
        instructions: "",
        precautions: "",
    }

    componentDidMount() {
        this.loadDrugs();
    }

    loadDrugs = () =>  {
        API.getDrugs()
        .then(res =>
            this.setState({ drugs: res.data})
            )
            .catch(err => console.log(err))
    };

    handleInputChange = event => {
        let value = event.target.value
        const name = event.target.name

        this.setState({
            [name]: value
          })

        }

        render() {
            return (
                Nav
            )
        }

}

export default Reports;
